import UIKit
import Alamofire

enum CategoryRouter {
    case all(page: Int)
    case allCategory
}

extension CategoryRouter: APIInputBase {
    
    var headers: Alamofire.HTTPHeaders? {
        return nil
    }
    
    var url: String {
        return "\(BaseApiRequest.url)\(nettruyenPath.all)"
    }
    
    var method: Alamofire.HTTPMethod {
        switch self {
        case .all:
            return .get
        case .allCategory:
            return .get
        }
    }
    
    var encoding: Alamofire.ParameterEncoding {
        return method == .get ? URLEncoding.default : JSONEncoding.default
    }
    
    
    var parameters: [String : Any]? {
        switch self {
        case .all(let page):
            return ["page": page]
        case .allCategory:
            return [:]
        }
    }
}
